CREATE TABLE "URP_INSTRUCTION" (
	"TEACHER_NO" CHAR(5) NOT NULL, 
	"COURSE_NO" CHAR(8) NOT NULL, 
	"CLASSROOM_NO" CHAR(6) NOT NULL, 
	"CLASS_NO" CHAR(8) NOT NULL, 
	"INSTRUCT_TIME" CHAR(5) NOT NULL, 
	"INSTRUCT_TERM" CHAR(5) NOT NULL, 
	CONSTRAINT "PK_URP_INSTRUCTION" PRIMARY KEY ("TEACHER_NO", "COURSE_NO", "CLASSROOM_NO", "CLASS_NO", "INSTRUCT_TIME", "INSTRUCT_TERM"), 
	CONSTRAINT "FK_URP_INSTRUCTION_CLASS_NO" FOREIGN KEY ("CLASS_NO") REFERENCES "URP_CLASS" ("CLASS_NO"), 
	CONSTRAINT "FK_URP_INSTRUCTION_COURSE_NO" FOREIGN KEY ("COURSE_NO") REFERENCES "URP_COURSE" ("COURSE_NO"), 
	CONSTRAINT "FK_URP_INSTRUCTION_TEACHER_NO" FOREIGN KEY ("TEACHER_NO") REFERENCES "URP_TEACHER" ("TEACHER_NO"), 
	CONSTRAINT "FK_URP_INSTRUCTION_CLASSROM_NO" FOREIGN KEY ("CLASSROOM_NO") REFERENCES "URP_CLASSROOM" ("CLASSROOM_NO")
);

